INSERT INTO `wp_postmeta` (`meta_id`, `post_id`, `meta_key`, `meta_value`) VALUES
(3420, 5, 'atsiliepimas_2_atsiliepimo_tekstas', '„Buvo be galo įdomu per muzikos instrumentus kontaktuoti su vaikeliu ar\r\n						dainuojant užmegsti su juo ryšį. Taip pat patiko namų darbų užduotys.\r\n						Kvėpavimo pratimai pradžioje sekėsi sunkiau. Kitoms moterims patarčiau lankyti užsiėmimus, nes tai puiki proga išmokti dainelių,\r\n						kurias galėsi dainuoti ir vaikeliui gimus. Apskritai visa tai įnešė į mano gyvenimą\r\n						daugiau pozityvumo, pamokė taisyklingiau kvėpuoti. „Atsirišo” noras piešti ir\r\n						dainuoti<span class=\"smile\">&#9786;</span>”'),
